            v_moneda,
            now(),
            in_usuario_creador
        );
		SET v_id_transaccion = LAST_INSERT_ID() ;
    END IF ;
END IF ;

CLOSE cur_boleto ;

UPDATE cnt_boleto 
	SET id_nota_debito_transaccion = v_id_transaccion ,
		estado 					   = 'P' ,
        id_cliente				   = in_id_cliente,
		id_promotor				   = in_id_counter,
        factor_cambiario		   = in_factor,
        id_nota_debito			   = in_id_nota_debito ,
