BEGIN

	DECLARE v_id_plan_cuenta		INT(11) ;
	DECLARE v_cuenta				VARCHAR(40) ;
	DECLARE v_nro_plan_cuenta		BIGINT(20) ;
	DECLARE v_nro_plan_cuenta_padre	BIGINT(20)  ;
